import React, { useState } from 'react';
import './Header.css';

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Función para cerrar el menú en móvil al hacer clic
  const closeMobileMenu = () => {
    if (window.innerWidth <= 768) {
      setMenuOpen(false);
    }
  };

  return (
    <header className="header sticky">
      <div className="logo">
        <img src="/Lourdes_Miranda_Moreno.jpg" alt="Lourdes Miranda Moreno"/>
      </div>
    
      <button className="menu-toggle" onClick={toggleMenu}>
        ☰
      </button>
    
      <nav className={`nav-links ${menuOpen ? 'active' : ''}`}>
        <ul>
          <li><a href="#about" onClick={closeMobileMenu}>Sobre mí</a></li>
          <li><a href="#projects" onClick={closeMobileMenu}>Proyectos</a></li>
          <li><a href="#skills" onClick={closeMobileMenu}>Habilidades</a></li>
          <li><a href="#contact" onClick={closeMobileMenu}>Contacto</a></li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
